import { AlgorandClient } from '@algorandfoundation/algokit-utils'

async function main() {
  // Initialize AlgorandClient pointing to LocalNet
  const algorand = AlgorandClient.defaultLocalNet()

  console.log('=== Account Creation and Funding Example ===\n')

  // Create an account from environment variable
  // This will create a new account if the environment variable doesn't exist,
  // or retrieve an existing account if it does
  const accountName = 'MY_ACCOUNT'
  console.log(`Creating/retrieving account: ${accountName}`)

  const account = await algorand.account.fromEnvironment(accountName)
  console.log(`Account address: ${account.addr}`)

  // Check the account information
  let accountInfo = await algorand.account.getInformation(account.addr)
  console.log(`Initial balance: ${accountInfo.balance.algo} ALGO`)

  // Ensure the account is funded with at least 1 ALGO
  // This is useful for ensuring accounts have minimum balance requirements
  const fundingAccount = await algorand.account.localNetDispenser()
  const minBalance = (1).algo()

  console.log(`\nEnsuring account has at least ${minBalance.algo} ALGO...`)
  const result = await algorand.account.ensureFunded(
    account,
    fundingAccount,
    minBalance
  )

  if (result) {
    console.log(`Funded ${result.amountFunded.algo} ALGO`)
    console.log(`Transaction ID: ${result.transactionId}`)
  } else {
    console.log('Account already had sufficient funds')
  }

  // Check updated balance
  accountInfo = await algorand.account.getInformation(account.addr)
  console.log(`Final balance: ${accountInfo.balance.algo} ALGO`)
}

main().catch(console.error)

import { AlgorandClient } from '@algorandfoundation/algokit-utils'

async function main() {
  // Initialize AlgorandClient pointing to LocalNet
  const algorand = AlgorandClient.defaultLocalNet()

  console.log('=== Account Rekeying Example ===\n')

  // Get funding from dispenser
  const dispenser = await algorand.account.localNetDispenser()

  // Create the account to be rekeyed
  const originalAccount = algorand.account.random()
  await algorand.account.ensureFunded(originalAccount, dispenser, (1).algo())
  console.log(`Original account: ${originalAccount.addr}`)

  // Create the new authorization account
  const newAuthAccount = algorand.account.random()
  await algorand.account.ensureFunded(newAuthAccount, dispenser, (0.1).algo())
  console.log(`New auth account: ${newAuthAccount.addr}`)

  // Check account info before rekeying
  let accountInfo = await algorand.account.getInformation(originalAccount.addr)
  console.log(`\nBefore rekeying:`)
  console.log(`  Address: ${accountInfo.address}`)
  console.log(`  Auth address: ${accountInfo.authAddr || 'Not set (same as address)'}`)

  // Rekey the original account to be controlled by the new auth account
  // After this, all transactions from originalAccount must be signed by newAuthAccount
  console.log(`\nRekeying account...`)
  await algorand.account.rekeyAccount(originalAccount, newAuthAccount, {
    note: 'Rekeying for enhanced security',
  })
  console.log('Rekey complete!')

  // Verify the rekey was successful
  accountInfo = await algorand.account.getInformation(originalAccount.addr)
  console.log(`\nAfter rekeying:`)
  console.log(`  Address: ${accountInfo.address}`)
  console.log(`  Auth address: ${accountInfo.authAddr}`)

  // Now send a transaction from the original account
  // but signed by the new auth account
  console.log(`\nSending transaction from rekeyed account...`)
  await algorand.send.payment({
    sender: originalAccount.addr,
    receiver: originalAccount.addr,
    amount: (1).microAlgo(),
    signer: newAuthAccount.signer, // Must use new auth account to sign
  })
  console.log('Transaction successful! Rekey is working.')

  console.log('\nImportant: After rekeying, all future transactions from the original')
  console.log('account must be signed by the new authorization account.')
}

