import { getAlgoNodeConfig, getAlgoClient, getAlgoIndexerClient, AlgorandClient } from '@algorandfoundation/algokit-utils'

async function main() {
  console.log('=== AlgoNode Public Endpoint Configuration ===\n')

  // AlgoNode provides free public endpoints for MainNet and TestNet
  // This is useful for connecting to the network without running your own node

  console.log('MainNet Algod Configuration:')
  const mainnetAlgodConfig = getAlgoNodeConfig('mainnet', 'algod')
  console.log(`  Server: ${mainnetAlgodConfig.server}`)
  console.log(`  Port: ${mainnetAlgodConfig.port}`)
  console.log(`  Token: ${mainnetAlgodConfig.token}`)

  console.log('\nTestNet Algod Configuration:')
  const testnetAlgodConfig = getAlgoNodeConfig('testnet', 'algod')
  console.log(`  Server: ${testnetAlgodConfig.server}`)
  console.log(`  Port: ${testnetAlgodConfig.port}`)
  console.log(`  Token: ${testnetAlgodConfig.token}`)

  console.log('\nMainNet Indexer Configuration:')
  const mainnetIndexerConfig = getAlgoNodeConfig('mainnet', 'indexer')
  console.log(`  Server: ${mainnetIndexerConfig.server}`)
  console.log(`  Port: ${mainnetIndexerConfig.port}`)
  console.log(`  Token: ${mainnetIndexerConfig.token}`)

  console.log('\nTestNet Indexer Configuration:')
  const testnetIndexerConfig = getAlgoNodeConfig('testnet', 'indexer')
  console.log(`  Server: ${testnetIndexerConfig.server}`)
  console.log(`  Port: ${testnetIndexerConfig.port}`)
  console.log(`  Token: ${testnetIndexerConfig.token}`)

  // Create clients using the AlgoNode config
  console.log('\nCreating clients with AlgoNode configuration...')
  const algodClient = getAlgoClient(testnetAlgodConfig)
  const indexerClient = getAlgoIndexerClient(testnetIndexerConfig)

  // Test the connection
  console.log('\nTesting connection to TestNet...')
  const health = await algodClient.healthCheck().do()
  console.log('Algod health check:', health)

  const versions = await algodClient.versionsCheck().do()
  console.log('Algod versions:', versions)

  // You can also use AlgorandClient with AlgoNode
  console.log('\nCreating AlgorandClient for TestNet...')
  const algorand = AlgorandClient.testNet()

  const status = await algorand.client.algod.status().do()
  console.log(`TestNet Status:`)
  console.log(`  Last round: ${status.lastRound}`)
  console.log(`  Last consensus version: ${status.lastVersion}`)
  console.log(`  Time since last round: ${status.timeSinceLastRound}ns`)

  console.log('\nSuccessfully connected to AlgoNode endpoints!')
}

