import { microAlgos, algos, transactionFees } from '@algorandfoundation/algokit-utils'

async function main() {
  console.log('=== Algo Amount Conversions and Utilities ===\n')

  // AlgoKit Utils provides utilities for working with Algo amounts
  // in both microAlgos (base unit) and Algos (1 ALGO = 1,000,000 microAlgos)

  console.log('1. Creating amounts with microAlgos:')
  const amount1 = microAlgos(1)
  console.log(`  microAlgos(1) = ${amount1} = ${amount1.microAlgo} µALGO`)

  const amount2 = microAlgos(100)
  console.log(`  microAlgos(100).microAlgo = ${amount2.microAlgo}n`)

  const amount3 = microAlgos(1000)
  console.log(`  microAlgos(1000).algo = ${amount3.algo} ALGO`)

  const amount4 = microAlgos(100_000_000)
  console.log(`  microAlgos(100_000_000).algo = ${amount4.algo} ALGO`)

  console.log('\n2. Creating amounts with algos:')
  const amount5 = algos(100)
  console.log(`  algos(100) = ${amount5}`)
  console.log(`  algos(100).microAlgo = ${amount5.microAlgo}n`)
  console.log(`  algos(100).algo = ${amount5.algo} ALGO`)

  console.log('\n3. Transaction fees:')
  // Transaction fees are 1000 microAlgos per transaction by default
  const singleFee = transactionFees(1)
  console.log(`  transactionFees(1).microAlgo = ${singleFee.microAlgo}n`)

  const multipleFees = transactionFees(10)
  console.log(`  transactionFees(10).microAlgo = ${multipleFees.microAlgo}n`)

  console.log('\n4. Using Number.prototype extensions:')
  // AlgoKit Utils extends the Number prototype for convenience
  const amount6 = (100).algo()
  console.log(`  (100).algo().algo = ${amount6.algo} ALGO`)
  console.log(`  (100).algo().microAlgo = ${amount6.microAlgo}n`)

  const amount7 = (100).microAlgo()
  console.log(`  (100).microAlgo().algo = ${amount7.algo} ALGO`)
  console.log(`  (100).microAlgo().microAlgo = ${amount7.microAlgo}n`)

  console.log('\n5. Practical examples:')
  // Common use cases for amount utilities

  // Minimum balance requirement
  const minBalance = algos(0.1)
  console.log(`  Minimum balance: ${minBalance.algo} ALGO (${minBalance.microAlgo} µALGO)`)

  // Funding amount
  const fundingAmount = algos(10)
  console.log(`  Funding amount: ${fundingAmount.algo} ALGO (${fundingAmount.microAlgo} µALGO)`)

  // Small payment
  const payment = (1).microAlgo()
  console.log(`  Small payment: ${payment.microAlgo} µALGO (${payment.algo} ALGO)`)

  // Transaction group fees (5 transactions)
  const groupFees = transactionFees(5)
  console.log(`  Fee for 5 txns: ${groupFees.microAlgo} µALGO (${groupFees.algo} ALGO)`)

  console.log('\nThese utilities make it easy to work with Algo amounts')
  console.log('without worrying about conversion between microAlgos and Algos.')
}

main().catch(console.error)

import { AlgorandClient } from '@algorandfoundation/algokit-utils'
import appSpec from './artifacts/testing-app.arc56.json' assert { type: 'json' }

async function main() {
  // Initialize AlgorandClient pointing to LocalNet
  const algorand = AlgorandClient.defaultLocalNet()

  console.log('=== Basic Smart Contract App Creation ===\n')

  // Get a funded account
  const creator = await algorand.account.localNetDispenser()

  // Get the approval and clear state programs from the app spec
  const approvalProgram = appSpec.source.approval
  const clearStateProgram = appSpec.source.clear

  // Define the schema (storage requirements)
  const schema = {
    globalInts: 1,
    globalByteSlices: 1,
    localInts: 0,
    localByteSlices: 0,
  }

  console.log('Creating app...')
  const result = await algorand.send.appCreate({
    sender: creator.addr,
    approvalProgram,
    clearStateProgram,
    schema,
  })

  console.log(`App created successfully!`)
  console.log(`  App ID: ${result.appId}`)
  console.log(`  App Address: ${result.appAddress}`)
  console.log(`  Transaction ID: ${result.txIds[0]}`)

  console.log('\nApp successfully created and can now be called.')
}

main().catch(console.error)

import { AlgorandClient } from '@algorandfoundation/algokit-utils'
import * as fs from 'fs'
import * as path from 'path'

async function main() {
  console.log('=== Deploy Smart Contract App ===\n')

  // Initialize AlgorandClient for LocalNet
  const algorand = AlgorandClient.defaultLocalNet()

  // Get a funded account to deploy the app
  const creator = await algorand.account.fromEnvironment('CREATOR', (10).algo())
  console.log('Creator account:', creator.addr)

  // Load the app specification
  const appSpecPath = path.join(__dirname, 'artifacts', 'testing-app.arc56.json')
  const appSpec = JSON.parse(fs.readFileSync(appSpecPath, 'utf-8'))

  // Extract programs from app spec
  const approvalProgram = Buffer.from(appSpec.source.approval, 'base64').toString('utf-8')
  const clearStateProgram = Buffer.from(appSpec.source.clear, 'base64').toString('utf-8')

  // Define deployment metadata
  const metadata = {
    name: 'TestingApp',
    version: '1.0',
    updatable: true,
    deletable: false,
  }

  console.log('Deploying app with metadata:')
  console.log('  Name:', metadata.name)
  console.log('  Version:', metadata.version)
  console.log('  Updatable:', metadata.updatable)
  console.log('  Deletable:', metadata.deletable)
  console.log('')

  // Deploy the app using the appDeployer
  // This handles create, update, or replace logic automatically
  const deployResult = await algorand.appDeployer.deploy({
    createParams: {
      sender: creator.addr,
      approvalProgram,
      clearStateProgram,
      schema: {
        globalInts: appSpec.state.global.num_uints,
        globalByteSlices: appSpec.state.global.num_byte_slices,
        localInts: appSpec.state.local.num_uints,
        localByteSlices: appSpec.state.local.num_byte_slices,
      },
    },
    updateParams: {
      sender: creator.addr,
    },
    deleteParams: {
      sender: creator.addr,
    },
    metadata,
    deployTimeParams: {
      VALUE: 1, // Template variable substitution
    },
  })

  console.log('\nApp deployed successfully!')
  console.log('  App ID:', deployResult.appId)
  console.log('  App Address:', deployResult.appAddress)
  console.log('  Created Round:', deployResult.createdRound)
  console.log('  Updated Round:', deployResult.updatedRound)
  console.log('  Name:', deployResult.name)
  console.log('  Version:', deployResult.version)
  console.log('')

  // Retrieve apps by creator to verify deployment
  const apps = await algorand.appDeployer.getCreatorAppsByName(creator.addr)
  console.log('Apps created by', creator.addr + ':')
  Object.entries(apps.apps).forEach(([name, app]) => {
    console.log(`  ${name}:`)
    console.log(`    App ID: ${app.appId}`)
    console.log(`    Version: ${app.version}`)
    console.log(`    Updatable: ${app.updatable}`)
    console.log(`    Deletable: ${app.deletable}`)
  })

  console.log('\n=== Complete ===')
}

main().catch(console.error)

import { AlgorandClient } from '@algorandfoundation/algokit-utils'
import * as fs from 'fs'
import * as path from 'path'

async function main() {
  console.log('=== App Lifecycle: Create, Update, and Delete ===\n')

  // Initialize AlgorandClient for LocalNet
  const algorand = AlgorandClient.defaultLocalNet()

  // Get a funded account
  const creator = await algorand.account.fromEnvironment('CREATOR', (10).algo())
  console.log('Creator account:', creator.addr, '\n')

  // Load app specification
  const appSpecPath = path.join(__dirname, 'artifacts', 'testing-app.arc56.json')
  const appSpec = JSON.parse(fs.readFileSync(appSpecPath, 'utf-8'))
  const approvalProgram = Buffer.from(appSpec.source.approval, 'base64').toString('utf-8')
  const clearStateProgram = Buffer.from(appSpec.source.clear, 'base64').toString('utf-8')

  // === STEP 1: CREATE THREE APPS ===
  console.log('Step 1: Creating three apps...')

  const createParams = (name: string) => ({
    sender: creator.addr,
    approvalProgram,
    clearStateProgram,
    schema: {
      globalInts: appSpec.state.global.num_uints,
      globalByteSlices: appSpec.state.global.num_byte_slices,
      localInts: appSpec.state.local.num_uints,
      localByteSlices: appSpec.state.local.num_byte_slices,
    },
    note: JSON.stringify({
      name,
      version: '1.0',
      updatable: true,
      deletable: true,
    }),
  })

  const app1 = await algorand.send.appCreate(createParams('APP_1'))
  console.log('  Created APP_1:', app1.appId)

  const app2 = await algorand.send.appCreate(createParams('APP_2'))
  console.log('  Created APP_2:', app2.appId)

  const app3 = await algorand.send.appCreate(createParams('APP_3'))
  console.log('  Created APP_3:', app3.appId, '\n')

  // === STEP 2: UPDATE APP_1 ===
  console.log('Step 2: Updating APP_1 to version 2.0...')

  const updateResult = await algorand.send.appUpdate({
    sender: creator.addr,
    appId: app1.appId,
    approvalProgram,
    clearStateProgram,
    note: JSON.stringify({
      name: 'APP_1',
      version: '2.0',
      updatable: false,
      deletable: false,
    }),
  })
  console.log('  Updated APP_1 at round:', updateResult.confirmation?.confirmedRound, '\n')

  // === STEP 3: DELETE APP_3 ===
  console.log('Step 3: Deleting APP_3...')

  const deleteResult = await algorand.send.appDelete({
    sender: creator.addr,
    appId: app3.appId,
  })
  console.log('  Deleted APP_3 at round:', deleteResult.confirmation?.confirmedRound, '\n')

  // === STEP 4: VERIFY ALL APPS ===
  console.log('Step 4: Retrieving all apps created by this account...')

  // Wait a moment for indexer to catch up if using it
  await new Promise((resolve) => setTimeout(resolve, 1000))

  const apps = await algorand.appDeployer.getCreatorAppsByName(creator.addr)
  console.log('\nApps by name:')
  Object.entries(apps.apps).forEach(([name, app]) => {
    console.log(`  ${name}:`)
    console.log(`    App ID: ${app.appId}`)
    console.log(`    Version: ${app.version}`)
    console.log(`    Updatable: ${app.updatable}`)
    console.log(`    Deletable: ${app.deletable}`)
    console.log(`    Deleted: ${app.deleted}`)
  })

  console.log('\n=== Complete ===')
  console.log('Demonstrated full app lifecycle: create, update, and delete')
}

main().catch(console.error)

import { AlgorandClient } from '@algorandfoundation/algokit-utils'
import * as fs from 'fs'
import * as path from 'path'

async function main() {
  console.log('=== Update an Existing App ===\n')

  // Initialize AlgorandClient for LocalNet
  const algorand = AlgorandClient.defaultLocalNet()

  // Get a funded account
  const creator = await algorand.account.fromEnvironment('CREATOR', (10).algo())
  console.log('Creator account:', creator.addr, '\n')

  // Load app specification
  const appSpecPath = path.join(__dirname, 'artifacts', 'testing-app.arc56.json')
  const appSpec = JSON.parse(fs.readFileSync(appSpecPath, 'utf-8'))
  const approvalProgram = Buffer.from(appSpec.source.approval, 'base64').toString('utf-8')
  const clearStateProgram = Buffer.from(appSpec.source.clear, 'base64').toString('utf-8')

  // === STEP 1: DEPLOY INITIAL APP VERSION ===
  console.log('Step 1: Deploying initial version of app...')

  const initialMetadata = {
    name: 'MyUpdatableApp',
    version: '1.0',
    updatable: true, // Must be updatable to allow updates
    deletable: false,
  }

  const deployResult = await algorand.appDeployer.deploy({
    createParams: {
      sender: creator.addr,
      approvalProgram,
      clearStateProgram,
      schema: {
        globalInts: appSpec.state.global.num_uints,
        globalByteSlices: appSpec.state.global.num_byte_slices,
        localInts: appSpec.state.local.num_uints,
        localByteSlices: appSpec.state.local.num_byte_slices,
      },
    },
    updateParams: {
      sender: creator.addr,
    },
    deleteParams: {
      sender: creator.addr,
    },
    metadata: initialMetadata,
    deployTimeParams: {
      VALUE: 1,
    },
    onUpdate: 'update', // Allow updates
  })

  console.log('Initial deployment:')
  console.log('  App ID:', deployResult.appId)
  console.log('  Version:', deployResult.version)
  console.log('  Updatable:', deployResult.updatable, '\n')

  // === STEP 2: UPDATE THE APP ===
  console.log('Step 2: Updating app to version 2.0...')

  // Modify the code slightly (change template variable)
  const updatedMetadata = {
    name: 'MyUpdatableApp',
    version: '2.0',
    updatable: true,
    deletable: false,
  }

  const updateResult = await algorand.appDeployer.deploy({
    createParams: {
      sender: creator.addr,
      approvalProgram,
      clearStateProgram,
      schema: {
        globalInts: appSpec.state.global.num_uints,
        globalByteSlices: appSpec.state.global.num_byte_slices,
        localInts: appSpec.state.local.num_uints,
        localByteSlices: appSpec.state.local.num_byte_slices,
      },
    },
    updateParams: {
      sender: creator.addr,
    },
    deleteParams: {
      sender: creator.addr,
    },
    metadata: updatedMetadata,
    deployTimeParams: {
      VALUE: 2, // Changed value
    },
    onUpdate: 'update', // Perform update
  })

  console.log('After update:')
  console.log('  App ID:', updateResult.appId, '(same as before)')
  console.log('  Version:', updateResult.version)
  console.log('  Updated Round:', updateResult.updatedRound)
  console.log('  Operation performed:', 'transaction' in updateResult ? 'Updated' : 'No change needed')

  console.log('\n=== Complete ===')
  console.log('Successfully updated the app while preserving the same app ID')
}

