import { AlgorandClient } from '@algorandfoundation/algokit-utils'
import * as fs from 'fs'
import * as path from 'path'

async function main() {
  console.log('=== Replace App with Delete and Recreate ===\n')

  // Initialize AlgorandClient for LocalNet
  const algorand = AlgorandClient.defaultLocalNet()

  // Get a funded account
  const creator = await algorand.account.fromEnvironment('CREATOR', (10).algo())
  console.log('Creator account:', creator.addr, '\n')

  // Load app specification
  const appSpecPath = path.join(__dirname, 'artifacts', 'testing-app.arc56.json')
  const appSpec = JSON.parse(fs.readFileSync(appSpecPath, 'utf-8'))
  const approvalProgram = Buffer.from(appSpec.source.approval, 'base64').toString('utf-8')
  const clearStateProgram = Buffer.from(appSpec.source.clear, 'base64').toString('utf-8')

  // === STEP 1: DEPLOY INITIAL APP ===
  console.log('Step 1: Deploying initial app (deletable)...')

  const initialMetadata = {
    name: 'MyReplaceableApp',
    version: '1.0',
    updatable: false, // Not updatable - breaking changes needed
    deletable: true, // Must be deletable for replacement
  }

  const deployResult = await algorand.appDeployer.deploy({
    createParams: {
      sender: creator.addr,
      approvalProgram,
      clearStateProgram,
      schema: {
        globalInts: appSpec.state.global.num_uints,
        globalByteSlices: appSpec.state.global.num_byte_slices,
        localInts: appSpec.state.local.num_uints,
        localByteSlices: appSpec.state.local.num_byte_slices,
      },
    },
    updateParams: {
      sender: creator.addr,
    },
    deleteParams: {
      sender: creator.addr,
    },
    metadata: initialMetadata,
    deployTimeParams: {
      VALUE: 1,
    },
  })

  const originalAppId = deployResult.appId
  console.log('Initial deployment:')
  console.log('  App ID:', originalAppId)
  console.log('  Version:', deployResult.version)
  console.log('  Deletable:', deployResult.deletable, '\n')

  // === STEP 2: REPLACE THE APP ===
  console.log('Step 2: Replacing app with new version (breaking changes)...')
  console.log('This will delete the old app and create a new one with a different app ID\n')

  const replacementMetadata = {
    name: 'MyReplaceableApp', // Same name
    version: '2.0', // New version
    updatable: false,
    deletable: true,
  }

  const replaceResult = await algorand.appDeployer.deploy({
    createParams: {
      sender: creator.addr,
      approvalProgram,
      clearStateProgram,
      schema: {
        globalInts: appSpec.state.global.num_uints,
        globalByteSlices: appSpec.state.global.num_byte_slices,
        localInts: appSpec.state.local.num_uints,
        localByteSlices: appSpec.state.local.num_byte_slices,
      },
    },
    updateParams: {
      sender: creator.addr,
    },
    deleteParams: {
      sender: creator.addr,
    },
    metadata: replacementMetadata,
    deployTimeParams: {
      VALUE: 2, // Different code
    },
    onUpdate: 'replace', // Force replacement
  })

  console.log('After replacement:')
  console.log('  Old App ID:', originalAppId, '(deleted)')
  console.log('  New App ID:', replaceResult.appId, '(newly created)')
  console.log('  Version:', replaceResult.version)
  console.log('')

  console.log('Verification:')
  console.log('  App IDs are different:', originalAppId !== replaceResult.appId)
  console.log('  App name is same:', replacementMetadata.name)
  console.log('  Version updated:', replacementMetadata.version)

  console.log('\n=== Complete ===')
  console.log('Successfully replaced app by deleting and recreating')
  console.log('Use this approach when you have breaking changes that cannot be handled by update')
}

main().catch(console.error)

