import { AlgorandClient } from '@algorandfoundation/algokit-utils'

async function main() {
  console.log('=== Asset Clawback: Revoke Assets ===\n')

  const algorand = AlgorandClient.defaultLocalNet()
  const creator = await algorand.account.fromEnvironment('CREATOR', (10).algo())
  const holder = algorand.account.random()
  const clawback = algorand.account.random()
  await algorand.account.ensureFunded(holder, creator, (1).algo())
  await algorand.account.ensureFunded(clawback, creator, (1).algo())

  // Create asset with clawback enabled
  const assetResult = await algorand.send.assetCreate({
    sender: creator.addr,
    total: 1000n,
    decimals: 0,
    clawback: clawback.addr, // Enable clawback
  })
  console.log('Created asset with clawback:', assetResult.assetId, '\n')

  // Holder opts in
  await algorand.send.assetOptIn({
    sender: holder.addr,
    assetId: assetResult.assetId,
  })

  // Transfer assets to holder
  await algorand.send.assetTransfer({
    sender: creator.addr,
    assetId: assetResult.assetId,
    receiver: holder.addr,
    amount: 100n,
  })
  console.log('Transferred 100 assets to holder\n')

  // Clawback assets
  console.log('Clawing back 50 assets from holder...')
  const clawbackResult = await algorand.send.assetTransfer({
    sender: clawback.addr, // Clawback account signs
    assetId: assetResult.assetId,
    receiver: creator.addr, // Where to send clawed back assets
    amount: 50n,
    clawbackTarget: holder.addr, // From whom to clawback
  })
  console.log('Clawback complete! TX:', clawbackResult.txIds[0], '\n')

  // Verify balances
  const holderInfo = await algorand.asset.getAccountInformation(holder.addr, assetResult.assetId)
  const creatorInfo = await algorand.asset.getAccountInformation(creator.addr, assetResult.assetId)

  console.log('Asset balances after clawback:')
  console.log('  Holder:', holderInfo.balance)
  console.log('  Creator:', creatorInfo.balance)

  console.log('\n=== Complete ===')
}

