async function main() {
  console.log('=== Atomic Transaction Groups ===\n')

  const algorand = AlgorandClient.defaultLocalNet()
  const sender = await algorand.account.fromEnvironment('SENDER', (10).algo())
  const receiver1 = algorand.account.random()
  const receiver2 = algorand.account.random()

  console.log('Creating atomic transaction group...')
  console.log('Both payments will succeed or both will fail\n')

  const group = algorand.newGroup()

  group.addPayment({
    sender: sender.addr,
    receiver: receiver1.addr,
    amount: (1).algo(),
  })

  group.addPayment({
    sender: sender.addr,
    receiver: receiver2.addr,
    amount: (2).algo(),
  })

  const result = await group.send()

  console.log('Atomic group executed successfully!')
  console.log('  Group ID:', result.groupId)
  console.log('  Transaction IDs:', result.txIds)
  console.log('  Confirmations:', result.confirmations?.map(c => c?.confirmedRound))

  console.log('\n=== Complete ===')
  console.log('All transactions in the group succeeded atomically')
}

main().catch(console.error)

