    metadata,
    deployTimeParams: { VALUE: 1 },
  })
  console.log('  App ID:', result1.appId)
  console.log('  Created\n')

  // Second deployment with same parameters - should be idempotent
  console.log('Second deployment (same parameters)...')
  const result2 = await algorand.appDeployer.deploy({
