import { AlgorandClient } from '@algorandfoundation/algokit-utils'

async function main() {
  console.log('=== Indexer: Search Transactions with Pagination ===\n')

  const algorand = AlgorandClient.defaultLocalNet()
  const sender = await algorand.account.fromEnvironment('SENDER', (10).algo())

  // Send multiple transactions
  for (let i = 0; i < 5; i++) {
    await algorand.send.payment({
      sender: sender.addr,
      receiver: sender.addr,
      amount: (i + 1).microAlgo(),
    })
  }
  console.log('Sent 5 transactions\n')

  await new Promise(resolve => setTimeout(resolve, 2000))

  // Search transactions with pagination
  console.log('Searching transactions...')
  let nextToken: string | undefined
  let page = 1
  const limit = 2

  do {
    const searchResults = await algorand.client.indexer
      .searchForTransactions()
      .address(sender.addr)
      .limit(limit)
      .nextToken(nextToken || '')
      .do()

    console.log(`\nPage ${page}:`)
    searchResults.transactions.forEach((tx: any, i: number) => {
      console.log(`  ${i + 1}. TX: ${tx.id}`)
      console.log(`     Round: ${tx.confirmedRound}`)
    })

    nextToken = searchResults.nextToken
    page++
  } while (nextToken && page <= 3)

  console.log('\n=== Complete ===')
}

