    {
      version: 1,
      threshold: 2,
      addrs: [account1.addr, account2.addr, account3.addr],
    },
    [account1.account, account2.account] // Signers (2 out of 3)
  )

  console.log('Multisig address:', multisigAccount.addr, '\n')

  // Fund the multisig account
  await algorand.account.ensureFunded(multisigAccount, dispenser, (2).algo())
  console.log('Funded multisig account\n')

  // Send a payment from multisig account
  console.log('Sending payment from multisig account...')
  const result = await algorand.send.payment({
    sender: multisigAccount.addr,
    receiver: dispenser.addr,
    amount: (1).algo(),
    signer: multisigAccount.signer,
  })

  console.log('Payment sent from multisig!')
  console.log('  TX:', result.txIds[0])
  console.log('  Signed by 2 of 3 accounts')

  console.log('\n=== Complete ===')
}

