import { AlgorandClient } from '@algorandfoundation/algokit-utils'
import * as fs from 'fs'
import * as path from 'path'

async function main() {
  console.log('=== Handle Schema Breaking Changes ===\n')

  const algorand = AlgorandClient.defaultLocalNet()
  const creator = await algorand.account.fromEnvironment('CREATOR', (10).algo())

  const appSpecPath = path.join(__dirname, 'artifacts', 'testing-app.arc56.json')
  const appSpec = JSON.parse(fs.readFileSync(appSpecPath, 'utf-8'))
  const approvalProgram = Buffer.from(appSpec.source.approval, 'base64').toString('utf-8')
  const clearStateProgram = Buffer.from(appSpec.source.clear, 'base64').toString('utf-8')

  // Deploy with original schema
  console.log('Deploying app with original schema...')
  const originalSchema = {
    globalInts: appSpec.state.global.num_uints,
    globalByteSlices: appSpec.state.global.num_byte_slices,
    localInts: appSpec.state.local.num_uints,
    localByteSlices: appSpec.state.local.num_byte_slices,
  }

  const result1 = await algorand.appDeployer.deploy({
    createParams: {
      sender: creator.addr,
      approvalProgram,
      clearStateProgram,
      schema: originalSchema,
    },
    updateParams: { sender: creator.addr },
    deleteParams: { sender: creator.addr },
    metadata: { name: 'SchemaApp', version: '1.0', updatable: false, deletable: true },
    deployTimeParams: { VALUE: 1 },
    onSchemaBreak: 'replace', // Replace on schema break
  })
  console.log('  App ID:', result1.appId, '\n')

  // Deploy with breaking schema change
  console.log('Deploying with schema breaking change...')
  const brokenSchema = {
    ...originalSchema,
    globalByteSlices: originalSchema.globalByteSlices + 1, // Schema break!
  }

  const result2 = await algorand.appDeployer.deploy({
    createParams: {
      sender: creator.addr,
      approvalProgram,
      clearStateProgram,
      schema: brokenSchema,
    },
    updateParams: { sender: creator.addr },
    deleteParams: { sender: creator.addr },
    metadata: { name: 'SchemaApp', version: '2.0', updatable: false, deletable: true },
    deployTimeParams: { VALUE: 2 },
    onSchemaBreak: 'replace',
  })
  console.log('  Old App ID:', result1.appId, '(deleted)')
  console.log('  New App ID:', result2.appId, '(created)')
  console.log('  Schema change handled by replacement')

  console.log('\n=== Complete ===')
}

main().catch(console.error)

