import { AlgorandClient } from '@algorandfoundation/algokit-utils'

async function main() {
  console.log('=== Transaction Leases for Idempotency ===\n')

  const algorand = AlgorandClient.defaultLocalNet()
  const sender = await algorand.account.fromEnvironment('SENDER', (10).algo())
  const receiver = algorand.account.random()

  const lease = 'unique-payment-lease'
  console.log('Using lease:', lease, '\n')

  // Send transaction with lease
  console.log('Sending first transaction with lease...')
  const result1 = await algorand.send.payment({
    sender: sender.addr,
    receiver: receiver.addr,
    amount: (1).algo(),
    lease,
  })
  console.log('First transaction sent:', result1.txIds[0], '\n')

  // Try to send duplicate transaction with same lease
  console.log('Attempting duplicate transaction with same lease...')
  try {
    await algorand.send.payment({
      sender: sender.addr,
      receiver: receiver.addr,
      amount: (1).algo(),
      lease,
    })
    console.log('ERROR: Duplicate should have been rejected!')
  } catch (error: any) {
    console.log('Duplicate correctly rejected!')
    console.log('  Error: Transaction with same lease already in ledger\n')
  }

  // Different lease works fine
  console.log('Sending transaction with different lease...')
  const result2 = await algorand.send.payment({
    sender: sender.addr,
    receiver: receiver.addr,
    amount: (1).algo(),
    lease: 'different-lease',
  })
  console.log('Different lease accepted:', result2.txIds[0])

  console.log('\n=== Complete ===')
  console.log('Leases prevent duplicate transactions')
}

main().catch(console.error)

