import { AlgorandClient } from '@algorandfoundation/algokit-utils'

async function main() {
  console.log('=== Custom Error Transformation ===\n')

  const algorand = AlgorandClient.defaultLocalNet()
  const sender = await algorand.account.fromEnvironment('SENDER', (10).algo())
  const receiver = algorand.account.random()

  const composer = algorand.newGroup()

  // Register a custom error transformer
  composer.registerErrorTransformer(async (error: Error) => {
    if (error.message.includes('overspend')) {
      return new Error('Custom Error: Insufficient balance for this transaction')
    }
    return error
  })

  // Add a payment that will fail
  composer.addPayment({
    sender: receiver.addr, // Unfunded account
    receiver: sender.addr,
    amount: (100).algo(),
  })

  console.log('Simulating transaction with error...')
  try {
    await composer.simulate()
  } catch (error: any) {
    console.log('Error caught and transformed!')
    console.log('  Original error would have been technical')
    console.log('  Transformed error:', error.message)
  }

  console.log('\n=== Complete ===')
  console.log('Error transformers provide user-friendly error messages')
}

main().catch(console.error)

