    })
    assetIds.push(result.assetId)
    console.log(`  Created asset \${i + 1}:`, result.assetId)
  }
  console.log('')

  // Bulk opt-in
  console.log('Performing bulk opt-in...')
  const optInResult = await algorand.asset.bulkOptIn(account, assetIds)

  console.log('Bulk opt-in complete!')
  console.log('  Opted into', assetIds.length, 'assets')
  console.log('  Transaction IDs:', optInResult.map(r => r.transactionId))

